import React, { PureComponent } from 'react';
import { Redirect } from 'react-router-dom';
import GoogleLogin from 'react-google-login';
import postData from '../postData';

class Sociallogin extends PureComponent {
  constructor(props) {
    super(props);
    this.state = {
      redirect: false,
    };
  }
  signup = (userToken, serviceProvider) => {
    let userData;
    if (serviceProvider === 'google/') {
      userData = {
        user: {
          access_token: userToken,
        },
      };
    }
    if (userData) {
      postData(serviceProvider, userData).then((result) => {
        console.log(result.data.user.access_token);
        localStorage.setItem('token', result.data.user.access_token);
        this.setState({redirect: true});
        console.log(result.status);
        console.log(this.props);
        // I shall use local storage fo the token
      });
    }
  }

  googleResponse = (response) => {
    const token = response.getAuthResponse().id_token;
    this.signup(token, 'google/');
  }

  render() {
