import React, { PureComponent } from 'react';
import { Redirect } from 'react-router-dom';
import GoogleLogin from 'react-google-login';
import { GoogleLoginButton } from 'react-social-login-buttons';

import postData from '../postData';

class Sociallogin extends PureComponent {
  go = { client_id: process.env.clientId }

  constructor(props) {
    super(props);
    this.state = {
      redirect: false,
    };
  }

  signup = (userToken, serviceProvider) => {
    let userData;
    if (serviceProvider === 'google/') {
      userData = {
        user: {
          access_token: userToken,
        },
      };
    }

    if (userData) {
      postData(serviceProvider, userData).then((result) => {
        console.log(result);
        localStorage.setItem('token', result.data.user.access_token);
        this.setState({ redirect: true });
      });
    }
  }

  googleResponse = (response) => {
    const token = response.getAuthResponse().id_token;
    this.signup(token, 'google/');
  }

  render() {
    if (this.state.redirect) {
      const to = { pathname: '/' };
      return (
        <Redirect to={to} />
      );
    }
    return (
      <div>
        <GoogleLogin
          clientId="1040550554735-0lfo665jrpgkprjkqdvh9njlc46mu6rg.apps.googleusercontent.com"
          buttonText="Login with Google"
          redirectUri="/"
          onSuccess={this.googleResponse}
          onFailure={this.googleResponse}
        >

        </GoogleLogin>
      </div>

    );
  }
}
export default Sociallogin;
