import React, { Component } from 'react';
// import {Redirect } from 'react-router';
import GoogleLogin from 'react-google-login';
import postData from './postData';

class Sociallogin extends Component {
  googleResponse = (response) => {
    const token = response.getAuthResponse().id_token;
    this.signup(token, 'google/');
  };

  signup(userToken, serviceProvider) {
    let userData;
    if (serviceProvider === 'google/') {
      userData = {
        user: {
          access_token: userToken,
        },
      };
    }
    if (userData) {
      postData(serviceProvider, userData).then((result) => {
        console.log(result);
        // I shall use local storage fo the token
      });
    }
  }
  render() {
    return (
      <div>
        <GoogleLogin
          clientId="1040550554735-0lfo665jrpgkprjkqdvh9njlc46mu6rg.apps.googleusercontent.com"
          theme="light"
          className=""
          buttonText="Login with Google"
          onSuccess={googleResponse}
          onFailure={googleResponse}
        />
      </div>
    );
  }
}
export default Sociallogin;
