import React, { PureComponent } from 'react';
import { Redirect } from 'react-router-dom';
import { connect } from 'react-redux';
import GoogleLogin from 'react-google-login';
import PropTypes from 'prop-types';

import { googleLoginUser } from '../../actions/userActions';

class GoogleloginButton extends PureComponent {
  constructor(props) {
    super(props);
    this.state = {
      redirect: false,
    };
  }

  componentWillReceiveProps(nextProps) {
    if (nextProps.isLoggedIn) {
      console.log('is working', nextProps.isLoggedIn);
      this.setState({
        redirect: true,
      });
    }
  }

  signup = (userToken, serviceProvider = '/api/auth/google/') => {
    const userData = {
      user: {
        access_token: userToken,
      },
    };

    const { googleLoginUserAction } = this.props;
    googleLoginUserAction(serviceProvider, userData);
  }

  googleResponse = (response) => {
    const token = response.tokenId;
    this.signup(token, '/api/auth/google/');
  }

  render() {
    const value = this.state;
    if (value.redirect) {
      const to = { pathname: '/' };
      return (
        <Redirect to={to} />
      );
    }
    return (
      <div>
        <GoogleLogin
          clientId="1040550554735-0lfo665jrpgkprjkqdvh9njlc46mu6rg.apps.googleusercontent.com"
          redirectUri="/"
          onSuccess={this.googleResponse}
          onFailure={this.googleResponse}
          className="google"
        >
          <i className="fa fa-google-plus  icon-with-margin" />
          <span className="raised-font"> Google</span>

        </GoogleLogin>
        &nbsp;

      </div>

    );
  }
}

const mapStateToProps = (state) => ({
  isLoggedIn: state.user.isLoggedIn,
  loading: state.user.loading,
});

GoogleloginButton.propTypes = {
  isLoggedIn: PropTypes.bool.isRequired,
  googleLoginUserAction: PropTypes.func.isRequired,
};

export { GoogleloginButton as GoogleTest };
export default connect(
  mapStateToProps, { googleLoginUserAction: googleLoginUser },
)(GoogleloginButton);
