import React, { PureComponent } from 'react';
import { Redirect } from 'react-router-dom';
import { connect } from 'react-redux';
import GoogleLogin from 'react-google-login';

import postData from '../postData';
import googleAuth from '../../actions/userActions';

class Googlelogin extends PureComponent {
  constructor(props) {
    super(props);
    this.state = {
      redirect: false,
    };
  }

  signup = (userToken, serviceProvider) => {
    let userData;
    if (serviceProvider === 'google/') {
      userData = {
        user: {
          access_token: userToken,
        },
      };
    }

    if (userData) {
      postData(serviceProvider, userData).then((result) => {
        console.log(result);
        // localStorage.setItem('token', result.data.user.access_token);
        this.setState({ redirect: true });
      });
    }
  }

  googleResponse = (response) => {
    const token = response.tokenId;
    this.signup(token, 'google/');
  }

  render() {
    if (this.state.redirect) {
      const to = { pathname: '/' };
      return (
        <Redirect to={to} />
      );
    }
    return (
      <div>
        <GoogleLogin
          clientId="1040550554735-0lfo665jrpgkprjkqdvh9njlc46mu6rg.apps.googleusercontent.com"
          redirectUri="/"
          onSuccess={this.googleResponse}
          onFailure={this.googleResponse}
          className="google"
        >
          <i className="fa fa-google-plus  icon-with-margin" />
          <span className="raised-font"> Google</span>

        </GoogleLogin>
        &nbsp;

      </div>

    );
  }
}

// const mapStateToProps = (state) => ({
// });


export default connect(null, { googleLoginUser})(Googlelogin);
