import React, { PureComponent } from 'react';
// import { Redirect } from 'react-router-dom';
import FacebookLogin from 'react-facebook-login';
import postData from '../postData';

class Facebook extends PureComponent {
  constructor(props) {
    super(props);
    this.state = {
      redirect: false,
    };
  }

  signup = (userToken, serviceProvider) => {
    let userData;
    userData = {
      user: {
        access_token: userToken,
      },
    };
    if (userData) {
      postData(serviceProvider, userData).then((result) => {
        console.log(result);
        localStorage.setItem('token', result.data.user.access_token);
        this.setState({ redirect: true });
      });
    }
  }

  responseFacebook = (response) => {
    const token = response.accessToken;
    this.signup(token, 'facebook/');
  }

  render() {
    if (this.state.redirect) {
      const to = { pathname: '/' };
      return (
        <Redirect to={to} />
      );
    }
    return (
      <FacebookLogin
        appId="2147542838793839"
        fields="name,email,picture"
        // size="small"
        callback={this.responseFacebook}
        icon="fa fa-facebook-square icon-with-margin"
        cssClass="facebook"
        textButton="Facebook"

      />


    );
  }
}
export default Facebook;
